import React, { Component } from 'react';
import _ from 'lodash';
import Chart from 'chart.js';
import MockDadosChart from 'app/mocks/MockDadosChart';

class GeraChart extends Component{
    render(){
        let dataSet = this.props.dados;

        let canvas = document.getElementById("canvas");
        let chart = new Chart(canvas, {
            type: "bar",
            data: {
                labels: _.map(MockDadosChart, (item) => { return item.dia }),
                datasets: [{
                    label: "Quantidade no dia",
                    data: _.map(MockDadosChart, (item) => { return item.qtde }),
                    backgroundColor: _.map(MockDadosChart, () => { return '#f44336f0'}),
                    borderColor: _.map(MockDadosChart, () => { return '#f44336' }),
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    yAxes: [{
                        ticks: {
                            beginAtZero: true
                        }
                    }]
                }
            }
        })

        return (<canvas id="chart" width={this.props.largura} height={this.props.altura} ></canvas>)
    }
}

class ViewGraficos extends Component {
    render() {
        return (
            <div style={{ display: this.props.visivel ? 'block' : 'none' }}>
                <div className="grafico">
                    <GeraChart dados={MockDadosChart} largura="400" altura="600" />
                </div>
            </div>
        )
    }
}

export default ViewGraficos;
