import { combineReducers } from 'redux';
import cadastrarUsuario from 'app/reducers/CadastroReducer';

const initialState = {
    menuVisivel: true
};

export const toggleMenu = (state = initialState, action) => {
    if (!action.menuVisivel) return state;
    state.menuVisivel = action.menuVisivel || state.menuVisivel;
    return state;
}

const MainReducer = combineReducers({
    cadastrarUsuario, toggleMenu
})

export default MainReducer;
