import MainStore from 'app/storoes/MainStore';

let currentValue;
const handleChange = () => {
    let previousValue = currentValue;
    currentValue = MainStore.getState();

    if(previousValue !== currentValue){
        console.log(`Este valor ${previousValue} foi alterado para ${currentValue}`);
    }
}

export default handleChange;
