import React, { Component } from 'react';
import Menu from './partials/Menu';
import MockDados from 'app/stores/MockDados';
import _ from 'lodash';
import Rodape from './partials/Rodape';

class ViewNoticias extends Component{
    render() {
        return(
            <div style={{ display: this.props.visivel ? 'block' : 'none' }}>
                <div className="noticias">
                    <h1 className="descricao-filtro-noticias">Filtrando notícias por: Sua escolha</h1>
                    <VisualizacaoDados />
                </div>
            </div>
        )
    }
}

class VisualizacaoDados extends Component {
    extenderDescricao = id => {
        let noticia = document.getElementById(`noticia-${id}`);
        debugger;
        return 'block';
    }

    toggleVisualizacao = noticia => {
        noticia.classList.toggle("ver-noticia");
    }

    render(){
        let noticias = [];
        _.forEach(MockDados, (dado, key) => {
            noticias.push(
                <article key={key} className="noticia">
                    <span className="imagem-noticia" onClick={(e) => this.toggleVisualizacao(e.currentTarget.parentElement)}>
                        <img src={dado.miniatura} alt="" />
                    </span>
                    <span className="descricao-noticia" style={{ display: this.extenderDescricao(key) }} >
                        <h3>{dado.titulo}</h3>
                        <p>{_.truncate(dado.descricao, { 'length': 120, 'separator': ' ' })}</p>
                    </span>
                </article>
            );
        })
        return noticias
    }
}

class ViewGrafico extends Component{
    render(){
        return(
            <div style={{ display: this.props.visivel ? 'block' : 'none' }}>
                <div className="grafico">
                    <h1>Tela com os gráficos</h1>
                </div>
            </div>
        )
    }
}

class MenuDashboard extends Component {
    changeView = nomeView => this.props.changeView(nomeView);

    render(){
        return(
            <div>
                <div className="barra-topo">
                    <img src="http://placehold.it/100x100" alt="" />
                    <h1>Mundi Oculos</h1>
                </div>
                <nav className="secoes-dashboard">
                    <ul>
                        <li className={this.props.view === "noticias" ? 'view-atual' : ''}>
                            <span onClick={() => this.changeView({ view: "noticias" })}>Sua escolha</span>
                        </li>
                        <li className={this.props.view === "grafico" ? 'view-atual' : ''}>
                            <span onClick={() => this.changeView({ view: "grafico" })}>Gráfico</span>
                        </li>
                        <li className={this.props.view === "regiao" ? 'view-atual' : ''}>
                            <span onClick={() => this.setState({ view: "regiao" })}>Na região</span>
                        </li>
                        <li className={this.props.view === "pais" ? 'view-atual' : ''}>
                            <span onClick={() => this.setState({ view: "pais" })}>No país</span>
                        </li>
                    </ul>
                </nav>
            </div>
        )
    }
}

class Dashboard extends Component{
    constructor(props){
        super(props);
        this.state = { view: "noticias" };
    }

    changeView = nomeView => {
        this.setState(nomeView);
    }

    render(){
        return(
            <div>
                <div className="main-layout">
                    <Menu ativo="dashboard" />
                    <div className="main-container dashboard">
                        <MenuDashboard changeView={this.changeView} view={this.state.view} />
                        <ViewNoticias visivel={this.state.view === "noticias"} />
                        <ViewGrafico visivel={this.state.view === "grafico"} />
                    </div>
                </div>
                <Rodape />
            </div>
        )
    }
}

export default Dashboard;
