import React, { Component } from 'react';
import MockNoticias from 'app/mocks/MockNoticias';
import _ from 'lodash';

class VisualizacaoDados extends Component {
    toggleVisualizacao = noticia => {
        _.map(document.getElementsByClassName("noticia"), (el) => {
            if (el.classList.contains("ver-noticia")) {
                el.classList.toggle("ver-noticia"); 
            }
        })

        noticia.classList.toggle("ver-noticia");
        let outrasNoticias = document.querySelectorAll(".noticia:not(.ver-noticia)");
        _.map(outrasNoticias, (el) => {
            let descricao = el.children.item(el.children.length - 1);
            descricao.style.opacity = noticia.classList.contains("ver-noticia") ? 0 : 1;
        });

        let noticiaVizinha = _.find(outrasNoticias, (el) => { return el.offsetTop === noticia.offsetTop });
    }

    render() {
        let noticias = [];
        _.forEach(MockNoticias, (dado, key) => {
            noticias.push(
                <article key={key} className="noticia">
                    <span className="imagem-noticia" onClick={(e) => this.toggleVisualizacao(e.currentTarget.parentElement)}>
                        <img src={dado.miniatura} alt="" />
                    </span>
                    <span className="descricao-noticia">
                        <p className="dica-imagem">Clique na imagem para expandi-la.</p>
                        <h3>{dado.titulo}</h3>
                        <p>{_.truncate(dado.descricao, { 'length': 120, 'separator': ' ' })}</p>
                    </span>
                </article>
            );
        })
        return noticias
    }
}

class ViewNoticias extends Component {
    render() {
        return (
            <div style={{ display: this.props.visivel ? 'block' : 'none' }}>
                <div className="noticias">
                    <VisualizacaoDados />
                </div>
            </div>
        )
    }
}

export default ViewNoticias;
