import { combineReducers } from 'redux';
import cadastro from './CadastroReducer';

const initialState = {
    menuVisivel: false
};

const toggleMenu = (state = initialState, action) => {
    state.menuVisivel = action.menuVisivel;
    return state;
}

const MainReducer = combineReducers({
    cadastro, toggleMenu
})
export default MainReducer;
