//import MainStore from "../stores/MainStore";

//import CADASTRAR_USUARIO from 'app/actions/CadastroActions';

//actions
export const CADASTRAR_USUARIO = 'CADASTRAR_USUARIO';

export const cadastrarUsuario = usuario => {
    return { type: CADASTRAR_USUARIO, usuario }
}

//reducers

const initialState = {
    usuario: null
};

const _cadastrarUsuario = (state = initialState, action) => {
    if(!action.usuario) return state;
    state.usuario = action.usuario;
    return state;
};

export default cadastrarUsuario;
