import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { Provider } from 'react-redux';
import 'assets/css/estilos.css';
import AppIndex from 'app/templates/Index.js';
import { CadastroView } from 'app/templates/Cadastro.js';
import MainStore from 'app/stores/MainStore';
import ToggleMenu from 'app/controllers/MainController';

class MainRouter extends Component{
    constructor(props){
        super(props);
        this.state = { menuVisivel: true }
    }

    render() {
        MainStore.dispatch(ToggleMenu({ menuVisivel: false }));
        console.log(MainStore.getState());
        return (
            <Provider store = { this.props.store } >
                <Router>
                    <div>
                        <nav className="menu" style={{ display: this.state.menuVisivel ? 'block' : 'none' }}>
                            <ul>
                                <li><Link to="/">Home</Link></li>
                            </ul>
                        </nav>
                        <div className="main">
                            <Route exact path="/" component={AppIndex} />
                            <Route path="/cadastro" component={CadastroView} />
                        </div>
                    </div>
                </Router>
            </Provider >
        );
    }
}

export default MainRouter;
