import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash';

class CustomModal extends Component{
    constructor(props){
        super(props)
        this.container = {};
    }

    componentDidMount = () => {
        this.popup = document.createElement("div");
        this.popup.style.display = this.props.visivel ? 'block' : 'none';
        let classesModal = _.split(this.props.className, ' ');
        _.map(classesModal, (classe) => this.popup.classList.add(classe) );
        this.container = document.getElementsByClassName("main").item(0);
        container.appendChild(this.popup);
        this._renderLayer();
    }

    componentDidUpdate = () => this._renderLayer();


    componentWillUnmount = () => {
        React.unmountComponentAtNode(this.popup);
        this.container.removeChild(this.popup);
    }

    _renderLayer = () => ReactDOM.render(this.props.children, this.popup);

    render = () => {
        return (
            <div style={{ display: this.props.visivel ? 'block' : 'none' }}>
                { this.props.children }
            </div>
            )
    };
}
export default CustomModal;
