//http://jamesknelson.com/rendering-react-components-to-the-document-body/
import React from 'react';

class RenderInBody extends React.Component{

    componentDidMount = () => {
        this.popup = document.createElement("div");
        document.body.appendChild(this.popup);
        this._renderLayer();
    }

    componentDidUpdate = () => this._renderLayer();


    componentWillUnmount = () => {
        React.unmountComponentAtNode(this.popup);
        document.body.removeChild(this.popup);
    }

    _renderLayer = () => React.render(this.props.children, this.popup);

    render = () => { return React.DOM.div(this.props) };

}

export default RenderInBody;
