import React, { Component } from 'react';
import _ from 'lodash';
import Moment from 'moment';
import Chart from 'chart.js';
import MockDadosChart from 'app/mocks/MockDadosChart';

class GeraChart extends Component{
    componentDidMount = () => {
        let dataSet = require('../../mocks/ojogo');
        let labelsChart = _.map(dataSet.registros, (registro) => { 
            return Moment(registro.dia + " " + registro.hora).format("MM/DD/YYYY HH:mm");
        });
        let dadosChart = _.map(dataSet.registros, (registro) => { return registro.termos });

        let canvas = document.getElementById("chart");
        let chart = new Chart(canvas, {
            type: "line",
            data: {
                labels: labelsChart,
                datasets: _.map(dadosChart, (dataset) => {
                    _.map(dataset, (item, palavraChave) => {
                        debugger;
                    })
                })/*[{
                    label: "Quantidade no dia",
                    data: _.map(dadosChart[0], (item) => {
                        return item;
                    }),
                    backgroundColor: 'rgba(244, 67, 54, .1)',
                    borderColor: '#f44336',
                    pointBackgroundColor: 'rgba(27, 135, 229, .3)',
                    pointBorderColor: '#1b87e5',
                    pointBorderWidth: 2,
                    borderWidt*h: 2,
                    pointRadius: 5
                }, {
                        label: "Quantidade no dia 1",
                        data: _.map(dadosChart[1], (item) => {
                            return item;
                        }),
                        backgroundColor: 'rgba(244, 67, 54, .1)',
                        borderColor: '#f44336',
                        pointBackgroundColor: 'rgba(27, 135, 229, .3)',
                        pointBorderColor: '#1b87e5',
                        pointBorderWidth: 2,
                        borderWidth: 2,
                        pointRadius: 5
                    }]*/
            },
            options: {
                elements: {
                    line: {
                        tension: 0
                    }
                }
            }
        });
        chart.render({
            duration: 600,
            lazy: false,
            easing: "easeOutExpo"
        })
    }

    render(){
        return (<canvas id="chart" width={this.props.largura} height={this.props.altura} ></canvas>)
    }
}

class ViewGraficos extends Component {
    render() {
        if (this.props.visivel) {
            return (
                <div>
                    <div className="grafico">
                        <GeraChart dados={MockDadosChart} largura="200" altura="100" />
                    </div>
                </div>
            )
        } else {
            return (<div></div>)
        }
    }
}

export default ViewGraficos;
