import MainStore from 'app/stores/MainStore';
import lodash from '../../../node_modules/lodash/lodash';

const handleChange = (value, handler) => {
    let state = MainStore.getState();

    if(lodash.find(value, state)){
        if (typeof handler !== 'function') {
            console.log(value);
        }
        handler(value);
    }
}

export default handleChange;
