const initialState = {
    menuVisivel: true
};

export const TOGGLE_MENU = 'TOGGLE_MENU';

export const toggleMenu = toggle => {
    return { type: TOGGLE_MENU, toggle }
}

const _toggleMenu = (state = initialState, action) => {
    if (!action.menuVisivel) return state;
    state.menuVisivel = action.menuVisivel || state.menuVisivel;
    console.log(state);
    return state;
}

export default toggleMenu;
