import React, { Component } from 'react';
import { connect } from 'react-redux';
import 'assets/css/estilos.css';

import MainStore from 'app/stores/MainStore';
import cadastrarUsuario from 'app/actions/CadastroActions';

class _cadastroView extends Component {
    constructor(props){
        super(props);
        console.log(props)
        this.state = {
            nome: '',
            sobrenome: '',
            telefone: '',
            email: ''
        };

        this.cadastrar = this.cadastrar.bind(this)
    }

    cadastrar = event => {
        event.preventDefault();
        MainStore.dispatch(cadastrarUsuario({nome: this.state.nome, sobrenome: this.state.sobrenome, telefone: this.state.telefone, email: this.state.email}));
    }

    setLabelFocused = nomeLabel => {
        let label = document.getElementsByTagName("label");
        console.log(label);
    }

    render() {
        return (
            <div>
                <div className="wrapper-cadastro">
                    <header>
                        <h1 className="menu-voltar"><a href="/"><i className="far fa-arrow-left"></i>&nbsp; Voltar</a></h1>
                    </header>
                    <h1 className="header-cadastro">Cadastre-se agora</h1>
                    <form onSubmit={this.cadastrar}>
                        <span>
                            <input id="nome" type="text" value={this.state.nome} onBlur={this.setLabelFocused("nome")} onChange={(e) => this.setState({ nome: e.target.value })} ></input><br />
                            <label htmlFor="nome">Nome: </label>
                            <div className="highlight-input"></div>
                        </span>

                        <span>
                            <input id="sobrenome" type="text" value={this.state.sobrenome} onBlur={this.setLabelFocused("sobrenome")} onChange={(e) => this.setState({ sobrenome: e.target.value })} ></input><br />
                            <label htmlFor="sobrenome">Sobrenome: </label>
                            <div className="highlight-input"></div>
                        </span>

                        <span>
                            <input id="telefone" type="text" value={this.state.telefone} onBlur={this.setLabelFocused("telefone")} onChange={(e) => this.setState({ telefone: e.target.value })} ></input><br />
                            <label htmlFor="telefone">Telefone: </label>
                            <div className="highlight-input"></div>
                        </span>

                        <span>
                            <input id="email" type="email" value={this.state.email} onBlur={this.setLabelFocused("email")} onChange={(e) => this.setState({ email: e.target.value })} ></input><br />
                            <label htmlFor="email">E-Mail: </label>
                            <div className="highlight-input"></div>
                        </span>

                        <input type="submit" name="Enviar" />
                    </form>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return { state }
};

const CadastroView = connect(mapStateToProps)(_cadastroView);
export default CadastroView;
