import { combineReducers } from 'redux';
import cadastro from './CadastroReducer';

const initialState = {
    menuVisivel: true
};

const TOGGLE_MENU = 'TOGGLE_MENU';

export const toggleMenu = toggle => {
    return { type: TOGGLE_MENU, toggle }
}

export const _toggleMenu = (state = initialState, action) => {
    if(!action || !action.menuVisivel) return state;
    state.menuVisivel = action.menuVisivel || state.menuVisivel;
    return state;
}

const MainReducer = combineReducers({
    cadastro, toggleMenu
})
export default MainReducer;
