import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Menu extends Component {

    toggleMenu = () => {
        let navBar = document.getElementsByClassName("menu").item(0);
        let container = document.getElementsByClassName("main-container").item(0);
        navBar.classList.toggle("menu-oculto");
        container.classList.toggle("main-container-full");
    }

    render() {
        var inputAtivo = document.getElementById(this.props.ativo);
        if (inputAtivo) {
            inputAtivo.classList.add("link-ativo");
        }
        return (
            <aside className="menu">
                <button className="botao-toggle-menu" onClick={() => this.toggleMenu()}><i className="fas fa-times"></i></button>
                <nav className="itens-menu">
                    <ul>
                        <li id="dashboard"><Link to="/dashboard">Início</Link></li>
                        <li id="configuracoes"><Link to="/configuracoes">Configurações</Link></li>
                    </ul>
                </nav>
            </aside>
        )
    }
}

export default Menu;
