import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { Provider, connect } from 'react-redux';
import 'assets/css/estilos.css';
import AppIndex from 'app/templates/Index.js';
import CadastroView from 'app/templates/Cadastro.js';

class _mainRouter extends Component{
    constructor(props){
        super(props);
    }

    render() {
        return (
            <Provider store = { this.props.store } >
                <Router>
                    <div>
                        <nav className="menu" style={{ display: this.props.menuVisivel ? 'block' : 'none' }}>
                            <ul>
                                <li><Link to="/">Home</Link></li>
                            </ul>
                        </nav>
                        <div className="main">
                            <Route exact path="/" component={AppIndex} />
                            <Route path="/cadastro" component={CadastroView} />
                        </div>
                    </div>
                </Router>
            </Provider >
        );
    }
}

const mapStateToProps = state => {
    debugger;
    return { menuVisivel: state.menuVisivel };
}

const mapDispatchToProps = dispatch => {
    return {
        toggleMenu: (toggle) => {
            console.log(dispatch, this);
        }
    }
}

const MainRouter = connect(mapStateToProps, mapDispatchToProps)(_mainRouter);
export default MainRouter;
