import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import _ from 'lodash';

class CustomModal extends Component{
    constructor(props){
        super(props)
    }

    componentDidMount = () => {
        this.popup = document.createElement("div");
        debugger;
        let classesModal = _.map(this.props.className, (item) => { return `"${item}"` }).join(',');
        this.popup.classList.add(classesModal);
        document.body.appendChild(this.popup);
        this._renderLayer();
    }

    componentDidUpdate = () => this._renderLayer();


    componentWillUnmount = () => {
        React.unmountComponentAtNode(this.popup);
        document.body.removeChild(this.popup);
    }

    _renderLayer = () => ReactDOM.render(this.props.children, this.popup);

    toggleModal = () => this.setState({ visible: !this.state.visible });

    render = () => {
        return (
            <div style={{ display: this.props.visivel ? 'block' : 'none' }}>{ this.props.children }</div>
            )
    };
}
export default CustomModal;
