import React, { Component } from 'react';
import Menu from './partials/Menu';
import MockDados from 'app/stores/MockDados';
import _ from 'lodash';

class VisualizacaoDados extends Component {
    constructor(props){
        super(props);
        this.state = { filtro: this.props.filtro || "geral" };
    }

    toggleVisualizacao = noticia => {
        noticia.classList.toggle("ver-noticia");
    }

    render(){
        let noticias = [];
        _.forEach(MockDados, (dado, key) => {
            noticias.push(
                <article key={key} className="noticia">
                    <span className="imagem-noticia" onClick={(e) => this.toggleVisualizacao(e.currentTarget.parentElement)}>
                        <img src={dado.miniatura} alt="" />
                    </span>
                    <div className="resumo-noticia">
                        <button className="fechar-visualizacao" onClick={(e) => this.toggleVisualizacao(e.currentTarget.parentElement)}><i className="fas fa-times"></i></button>
                        <p>{ dado.descricao }</p>
                    </div>
                    <span className="descricao-noticia">
                        <h3>{dado.titulo}</h3>
                        <p>{_.truncate(dado.descricao, { 'length': 120, 'separator': ' ' })}</p>
                    </span>
                </article>
            );
        })
        return noticias
    }
}

class MenuDashboard extends Component {
    constructor(props){
        super(props)
        this.state = { filtro: "" };
    }

    render(){
        return(
            <div>
                <div className="barra-topo">
                    <img src="http://placehold.it/100x100" alt="" />
                    <h1>Mundi Oculos</h1>
                </div>
                <nav className="secoes-dashboard">
                    <ul>
                        <li className="filtro-atual"><span onClick={() => this.setState({ filtro: "escolha" })}>Sua escolha</span></li>
                        <li><span onClick={() => this.setState({ filtro: "estado" })}>No estado</span></li>
                        <li><span onClick={() => this.setState({ filtro: "regiao" })}>Na região</span></li>
                        <li><span onClick={() => this.setState({ filtro: "pais" })}>No país</span></li>
                    </ul>
                </nav>
                <div className="noticias">
                    <h1 className="descricao-filtro-noticias">Filtrando notícias por: Sua escolha</h1>
                    <VisualizacaoDados filtro={this.state.filtro} />
                </div>
            </div>
        )
    }
}

class Dashboard extends Component{
    render(){
        return(
            <div className="main-layout">
                <Menu ativo="dashboard" />
                <div className="main-container dashboard">
                    <MenuDashboard />
                </div>
            </div>
        )
    }
}

export default Dashboard;
