import React, { Component } from 'react';
import 'assets/css/estilos.css';

import MainStore from 'app/stores/MainStore';
import { cadastrarUsuario } from 'app/actions/CadastroActions';

export class CadastroView extends Component {
    constructor(props){
        super(props);
        this.state = {
            nome: '',
            sobrenome: '',
            telefone: '',
            email: ''
        };

        this.cadastrar = this.cadastrar.bind(this.state)
    }

    cadastrar = usuario => MainStore.dispatch(cadastrarUsuario(usuario));

    render() {
        return (
            <div>
                <header>
                    <p>Teste</p>
                    <input type="text" value={this.state.nome}></input>
                    <button onClick={() => cadastrar()}>O jogo.</button>
                </header>
            </div>
        );
    }
}
