import React, { Component } from 'react';
import 'assets/css/estilos.css';

import MainStore from 'app/stores/MainStore';
import { cadastrarUsuario } from 'app/actions/CadastroActions';

export class CadastroView extends Component {
    constructor(props){
        super(props);
        this.state = {
            nome: '',
            sobrenome: '',
            telefone: '',
            email: ''
        };

        this.cadastrar = this.cadastrar.bind(this)
    }

    componentDidMount = () => {
        let menu = document.getElementsByClassName("menu");
        console.log(menu.item(0))
    }

    cadastrar = event => {
        event.preventDefault();
        MainStore.dispatch(cadastrarUsuario({nome: this.state.nome, sobrenome: this.state.sobrenome, telefone: this.state.telefone, email: this.state.email}));
    }

    render() {
        return (
            <div>
                <header>
                    <p>Teste</p>
                </header>
                <form onSubmit={this.cadastrar}>
                    <label htmlFor="nome">Nome: </label>
                    <input id="nome" type="text" value={this.state.nome} onChange={(e) => this.setState({ nome: e.target.value })} /><br />

                    <label htmlFor="sobrenome">Sobrenome: </label>
                    <input id="sobrenome" type="text" value={this.state.sobrenome} onChange={(e) => this.setState({ sobrenome: e.target.value })} /><br />

                    <label htmlFor="telefone">Telefone: </label>
                    <input id="telefone" type="text" value={this.state.telefone} onChange={(e) => this.setState({ telefone: e.target.value })} /><br />

                    <label htmlFor="email">E-Mail: </label>
                    <input id="email" type="email" value={this.state.email} onChange={(e) => this.setState({ email: e.target.value })} /><br />

                    <input type="submit" name="Enviar" />
                </form>
            </div>
        );
    }
}
