import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import { Provider, connect } from 'react-redux';
import 'assets/css/estilos.css';
import AppIndex from 'app/templates/Index';
import Cadastro from 'app/templates/Cadastro';
import Login from 'app/templates/Login';
import Dashboard from './Dashboard';
import Tela404 from './Tela404';

class _mainRouter extends Component{
    constructor(props){
        super(props);
        this.state = { menuVisivel: true }
    }

    toggleMenu = bool => {
        this.setState({ menuVisivel: bool })
    }

    render() {
        return (
            <Provider store = { this.props.store } >
                <Router>
                    <div className="main">
                        <Route exact path="/" component={AppIndex} />
                        <Route path="/cadastro" component={Cadastro} />
                        <Route path="/login" component={Login} />
                        <Route path="/dashboard" component={Dashboard} />
                        <Route path="/configuracao" component={Dashboard} />
                        <Route component={Tela404} />
                    </div>
                </Router>
            </Provider >
        );
    }
}

const mapStateToProps = state => {
    return { menuVisivel: state.menuVisivel || true };
}

const MainRouter = connect(mapStateToProps)(_mainRouter);
export default MainRouter;
