//http://jamesknelson.com/rendering-react-components-to-the-document-body/
import React from 'react';

var RenderInBody = React.createClass({

    componentDidMount: function () {
        this.popup = document.createElement("div");
        document.body.appendChild(this.popup);
        this._renderLayer();
    },


    componentDidUpdate: function () {
        this._renderLayer();
    },


    componentWillUnmount: function () {
        React.unmountComponentAtNode(this.popup);
        document.body.removeChild(this.popup);
    },


    _renderLayer: function () {
        React.render(this.props.children, this.popup);
    },


    render(){
        // Render a placeholder
        return React.DOM.div(this.props);
    }

});

export default RenderInBody;
