import { TOGGLE_MENU } from 'app/actions/IndexActions'

const initialState = {
    menuVisivel: true
};

const toggleMenu = (state = initialState, action) => {
    switch (action.type) {
        case TOGGLE_MENU:
            state.menuVisivel = action.toggle.menuVisivel;
            debugger;
            return state;
        default: return state;
    }
}

export default toggleMenu;
